```
package main
import (
	"fmt"
	"math/rand"
	"time"
)
var activities = []string{"Go for a walk in the park", "Watch a movie at home", "Try a new restaurant", "Play a board game", "Attend a concert or show", "Take a cooking class", "Visit a museum", "Go on a hike", "Take a scenic drive", "Have a picnic", "Try out different sports", "Explore a new city or town", "Attend a cultural festival or fair", "Take a dance or music class", "Visit a theme park"}
func main() {
	rand.Seed(time.Now().UnixNano())
	rand.Shuffle(len(activities), func(i, j int) {
		activities[i], activities[j] = activities[j], activities[i]
	})
	for i := 0; i < len(activities); i++ {
		fmt.Println("Activity", i+1, ":", activities[i])
	}
	fmt.Println("\nPlease enter your feedback on the suggested activities:")
	var feedback string
	fmt.Scanln(&feedback)
	fmt.Println("Thank you for your feedback! We will use it to improve our suggestions for future date nights.")
}
